  51 => '{"id":0,"warning":"","type":"addonsMustHave","name":"psproductstats","version":"2.0.2","tab":"analytics_stats","displayName":"Essential Product Statistics","description":"See key statistics for all your product pages, get marketing ideas and optimization tips to improve product performance and boost your sales, all with just a glance!","description_full":"See key statistics for all your product pages, get marketing ideas and optimization tips to improve product performance and boost your sales, all with just a glance!","author":"PrestaShop","limited_countries":[],"parent_class":"","onclick_option":false,"is_configurable":0,"need_instance":0,"not_on_disk":1,"available_on_addons":1,"trusted":1,"active":0,"additional_description":"With the Product Advanced Stats (Analytics &amp; Tips)&nbsp;module developed by PrestaShop, you can view all of the following for the time period of your choice: &nbsp; Information about sales and revenue generated by the product:A visual representation of your sales curveGross revenue generatedNet revenue generatedBuyer profile bySexAgeCountryPayment preferencesShipping preferences&nbsp; Compare this product&rsquo;s performance with other products in the same category for the designated period with respect to theNumber of products soldEarningsVisits and conversion rate (through Google Analytics)Cart abandonmentRefund rate","compatibility":{"from":"1.6.1.1","to":"1.7.8.8"},"nb_rates":null,"avg_rate":null,"badges":{"badge":"https:\\/\\/medias2.prestastore.com\\/themes\\/prestastore\\/img\\/front\\/sprites\\/badges\\/made-by-prestashop.png"},"url":{"0":"https:\\/\\/addons.prestashop.com\\/ru\\/analytics-statistics\\/27535-essential-product-statistics.html?utm_source=back-office&utm_medium=push-addons&utm_campaign=back-office-RU&utm_content=download"},"image":"..\\/img\\/tmp\\/f8a8a554768de5cf325ec8874a5a1c9b.jpg","addons_buy_url":"https:\\/\\/addons.prestashop.com\\/ru\\/analytics-statistics\\/27535-essential-product-statistics.html?ut